import { useState } from "react";
import ReactDOM from "react-dom/client";

function App() {
  const [brand, setBrand] = useState("Ford");
  const [model, setModel] = useState("Mustang");
  const [year, setYear] = useState("1964");
  const [color, setColor] = useState("red");

  const changeAttributes = () => {
    setBrand(prevBrand => prevBrand === "Ford" ? "Chevrolet" : "Ford");
    setModel(prevModel => prevModel === "Mustang" ? "Camaro" : "Mustang");
    setYear(prevYear => prevYear === "1964" ? "2020" : "1964");
    setColor(prevColor => prevColor === "red" ? "blue" : "red");
  };

  return (
    <>
      <h1>My {brand}</h1>
      <p>
        It is a {color} {model} from {year}.
      </p>
      <button type="button" onClick={changeAttributes}>Change Attributes</button>
    </>
  );
}

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(<App />);

export default App;

