import { useState } from "react";
import ReactDOM from "react-dom/client";

function App() {
  const brands = ["Ford", "Chevrolet", "Toyota"];
  const models = ["Mustang", "Camaro", "Corolla"];
  const years = ["1964", "2020", "2025"];
  const colors = ["red", "blue", "green"];

  const [brandIndex, setBrandIndex] = useState(0);
  const [modelIndex, setModelIndex] = useState(0);
  const [yearIndex, setYearIndex] = useState(0);
  const [colorIndex, setColorIndex] = useState(0);

  const changeAttributes = () => {
    setBrandIndex((prevIndex) => (prevIndex + 1) % brands.length);
    setModelIndex((prevIndex) => (prevIndex + 1) % models.length);
    setYearIndex((prevIndex) => (prevIndex + 1) % years.length);
    setColorIndex((prevIndex) => (prevIndex + 1) % colors.length);
  };

  return (
    <>
      <h1>My {brand}</h1>
      <p>
        It is a {color} {model} from {year}.
      </p>
      <button type="button" onClick={changeAttributes}>Change Attributes</button>
    </>
  );
}

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(<App />);

export default App;

